        alert(subDemo1.family); //哥哥,姐姐,爸爸,妈妈,爷爷,奶奶
        alert(subDemo2.family); //哥哥,姐姐,爸爸,妈妈
        //测试构造函数传递参数
        alert(subDemo1.name);
